mapper.select:
SELECT * FROM drain_rule_except
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND drain_rule_code = #{drainRuleCode}

mapper.insert:
INSERT INTO drain_rule_except (
gmt_created
, gmt_modified
, drain_rule_code
, except_topping_code
, tenant_code
) VALUES

    (
        now()
        , now()
        , #{po.drainRuleCode}
        , #{po.exceptToppingCode}
        , #{po.tenantCode}
    )

mapper.delete:
DELETE FROM drain_rule_except
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND drain_rule_code = #{drainRuleCode}
